mapper.select:
select photo_id as photo_id, school_id as school_id, class_id as class_id, photo_type as photo_type, team_type as team_type, photo_url as photo_url, photo_resize_url as photo_resize_url, add_date as add_date, create_date as create_date, create_by as create_by, update_date as update_date, update_by as update_by, version as version from kt_bus_photo where school_id = #{school_id}
and class_id = #{class_id}
and photo_type = #{photo_type}
and team_type = #{team_type}
and add_date like CONCAT(#{add_date}, '%')
and photo_id ${app_sql}
order by photo_id ${order_sql}

select
	add_date as add_date,
	school_id as school_id,
	class_id as class_id,
	team_type as team_type,
	create_date as create_date
from kt_bus_photo
where school_id = #{school_id}

	and class_id = #{class_id}


	and photo_type = #{photo_type}


	and team_type = #{team_type}


	and add_date like CONCAT(#{add_date}, '%')


	and photo_id  ${app_sql}

GROUP BY add_date

	order by photo_id ${order_sql}
